      var h = window.innerHeight;
      var w = window.innerWidth;
      var api = 'https://api.unsplash.com/photos/random';
      var cred = '?client_id=8893199052847965d3999199c4079cdf607d1f854c5485f43822beb93ef124c4';
      var query = '&query=landscape';
      var size = '&w=' + w + '&h=' + h;
      var url = api + cred + query + size;
      
      
      async function start() {
        var choice = await window.localStorage.getItem('quotes-choices')
        var source = bomba_quotes.concat(wilq_quotes);
        if (choice === "bomba") {
          source = bomba_quotes;
        }
        if (choice === "wilq") {
          source = wilq_quotes;
        }
        var sentence = source[Math.floor(Math.random() * (source.length + 1))];
        document.getElementById('quote').innerText = sentence;
      }

      function startTime() {
        var today = new Date();
        var h = today.getHours();
        var m = today.getMinutes();
        var s = today.getSeconds();
        m = checkTime(m);
        s = checkTime(s);
        document.getElementById('time').innerHTML =
        h + ":" + m + ":" + s;
        var t = setTimeout(startTime, 500);
      }

      function checkTime(i) {
        if (i < 10) {i = "0" + i};
        return i;
      }

      function saveImage(url) {
        console.log('saveImage');
        var oldURL = window.localStorage.getItem('next-img-url');
        if (url && oldURL !== url) {
          console.log('url', url);
          console.log('oldURL', oldURL);
          window.localStorage.setItem('next-img-url', url);
          var storedImgUrls = window.localStorage.getItem('img-urls') || '';
          window.localStorage.setItem('img-urls', storedImgUrls + url);
          var preloadImg = new Image();
          preloadImg.src = url.replace(/"/g, '');
          document.body.appendChild(preloadImg);
        }
      }

      function getImage() {
        console.log('getImage');
        return window.localStorage.getItem('next-img-url');
      }

      function getRandomOldImage() {
        console.log('getRandomOldImage');
        var ls = window.localStorage.getItem('img-urls') || "";
        var randomOldImages = ls.split('""');
        return randomOldImages[Math.floor(Math.random() * randomOldImages.length )];
      }

      function setImage(image) { 
        console.log('seTImage');
        var url = "url(" + image + ")";
        console.log('url', url);
        document.getElementById('content').style.backgroundImage = url;
      }

      function setFallbackImage() {
        window.localStorage.setItem('img-urls', 
        '"https://images.unsplash.com/photo-1537083702767-42f85f12834f?ixlib=rb-0.3.5&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=1520&h=1027&fit=crop&ixid=eyJhcHBfaWQiOjQyMDAzfQ&s=c82ef40fc32e1747cc0fde73d4ec1db2"'
        );  
      }

      async function handleImage(url) {
        console.log('handleImage');
        try {
          var img = getImage();
          if (!img) {
            saveImage(url);
            const loaded = await loadImage();
            console.log('loaded', loaded)
          } else {
            setImage(img);
            saveImage(url);
          }
        }
        catch (e) {
          console.log('ee', e);
        }
      }

      function loadImage() {
        console.log('loadImage');
        fetch(url)
          .then(response => response.status === 200 ? response.json() :  null )
          .then(json => json ? handleImage(JSON.stringify(json.urls.custom || json.urls.full)) : handleImage(getRandomOldImage()))
      }

      function handleForm(event) {
        console.log('handleForm');
        event.preventDefault();
        setFallbackImage();
        var inputs = document.querySelectorAll("input[type='checkbox']"); 
        var choices = '';
        for(var i = 0; i < inputs.length; i++) {
            if (inputs[i].checked) {choices = choices + (inputs[i].name)}
        }

        window.localStorage.setItem('quotes-choices', choices);

        document.getElementById('choice').style.opacity = '0';
        document.getElementById('time').style.opacity = '1';
        document.getElementById('quote').style.opacity = '1';
        setTimeout(() => {
          document.getElementById('choice').style.display = "none";
          document.getElementById('time').style.display = 'block';
          document.getElementById('quote').style.display = 'block';
          start();
        }, 2000);
      }

      window.onload = function () {
        document.getElementById('choice').addEventListener('submit', handleForm);
        try {
          var choice = null;
          if (Boolean((window.localStorage.getItem('quotes-choices')))) {
            document.getElementById('choice').style.display = "none";
            document.getElementById('time').style.display = 'block';
            document.getElementById('quote').style.display = 'block';
            document.getElementById('time').style.opacity = '1';
            document.getElementById('quote').style.opacity = '1';
          }
          start();
          startTime();
          loadImage();
        }
        catch (error) {
          console.log(error);
        }
      }
